mapper.insert:
INSERT INTO TB_BOARD(
	  IDX			/** 번호(PK) */
	, TITLE			/** 제목 */
	, CONTENT		/** 내용 */
	, WRITER		/** 작성자 */
	, PSWD			/** 비밀번호 */
	, VIEW_CNT		/** 조회수 */
	, NOTICE_YN		/** 공지글 여부 */
	, SECRET_YN		/** 비밀글 여부 */
	, DELETE_YN		/** 삭제 여부 */
	, INSERT_TIME	/** 등록 시간 */
	, UPDATE_TIME	/** 수정 시간 */
	, DELETE_TIME	/** 삭제 시간 */
	, PARENT_IDX	/** 부모번호 */
	, REORDER		/** 그룹 내 순서 */
) VALUES

	(
		  #{item.idx}
		, #{item.title}
		, #{item.content}
		, #{item.writer}
		, #{item.pswd}
		, 0
		, #{item.noticeYn, typeHandler=com.board.handler.BooleanHandler}
		, #{item.secretYn, typeHandler=com.board.handler.BooleanHandler}
		, 'N'
		, NOW()
		, NULL
		, NULL
		, #{item.parentIdx}
		, #{item.reorder}
	)

mapper.update:
UPDATE 
		TB_BOARD
   SET   
   		 UPDATE_TIME = NOW()
       , TITLE = #{title}
       , CONTENT = #{content}
       , WRITER = #{writer}
       , PSWD = #{pswd}
       , NOTICE_YN = #{noticeYn, typeHandler=com.board.handler.BooleanHandler}
       , SECRET_YN = #{secretYn, typeHandler=com.board.handler.BooleanHandler}
 WHERE 
 		IDX= #{idx}

mapper.select:
WITH RECURSIVE CTE AS ( SELECT IDX FROM TB_BOARD WHERE IDX = #{IDX}
UNION ALL
SELECT T.IDX FROM TB_BOARD T INNER JOIN CTE AS C ON T.PARENT_IDX = C.IDX ) SELECT IDX FROM CTE

mapper.update:
UPDATE
		TB_BOARD
   SET
   		NOTICE_YN = #{noticeYn, typeHandler=com.board.handler.BooleanHandler}
 WHERE
 		IDX = #{idx}
