mapper.select:
WITH RECURSIVE CTE AS ( SELECT IDX, PARENT_IDX, REORDER, TITLE, CONTENT, WRITER, VIEW_CNT, NOTICE_YN, SECRET_YN, DELETE_YN, INSERT_TIME, CONCAT(LPAD(IDX,(SELECT LENGTH(MAX(IDX)) FROM tb_board), '0')) AS PATH, 1 AS DEPTH FROM TB_BOARD WHERE PARENT_IDX = 0
UNION ALL
SELECT T.IDX, T.PARENT_IDX, T.REORDER, T.TITLE, T.CONTENT, T.WRITER, T.VIEW_CNT, T.NOTICE_YN, T.SECRET_YN, T.DELETE_YN, T.INSERT_TIME, CONCAT(C.PATH, '-', T.REORDER) AS PATH, C.DEPTH + 1 AS DEPTH FROM TB_BOARD T INNER JOIN CTE AS C ON T.PARENT_IDX = C.IDX ) SELECT *, ROW_NUMBER() OVER(ORDER BY SUBSTRING(PATH,1,(SELECT LENGTH(MAX(IDX)) FROM tb_board)) , PATH DESC) AS NUM FROM CTE WHERE DELETE_YN = 'N' ORDER BY NOTICE_YN DESC,SUBSTRING(PATH,1,(SELECT LENGTH(MAX(IDX)) FROM tb_board)) DESC, PATH LIMIT #{rowNum} OFFSET #{start}

mapper.insert:
INSERT INTO tb_board(
	  idx			/** 번호(PK) */
	, title			/** 제목 */
	, content		/** 내용 */
	, writer		/** 작성자 */
	, pswd			/** 비밀번호 */
	, view_cnt		/** 조회수 */
	, notice_yn		/** 공지글 여부 */
	, secret_yn		/** 비밀글 여부 */
	, delete_yn		/** 삭제 여부 */
	, insert_time	/** 등록 시간 */
	, update_time	/** 수정 시간 */
	, delete_time	/** 삭제 시간 */
	, parent_idx	/** 부모번호 */
	, reorder		/** 그룹 내 순서 */
) VALUES (
	  #{idx}
	, #{title}
	, #{content}
	, #{writer}
	, #{pswd}
	, 0
	, #{noticeYn, typeHandler=com.board.handler.BooleanHandler}
	, #{secretYn, typeHandler=com.board.handler.BooleanHandler}
	, 'N'
	, NOW()
	, NULL
	, NULL
	, #{parentIdx}
	, #{reorder}
)

mapper.update:
UPDATE 
		tb_board
   SET   
   		 update_time = NOW()
       , title = #{title}
       , content = #{content}
       , writer = #{writer}
       , pswd = #{pswd}
       , notice_yn = #{noticeYn, typeHandler=com.board.handler.BooleanHandler}
       , secret_yn = #{secretYn, typeHandler=com.board.handler.BooleanHandler}
 WHERE 
 		idx= #{idx}

mapper.select:
WITH RECURSIVE CTE AS ( SELECT IDX FROM TB_BOARD WHERE IDX = #{IDX}
UNION ALL
SELECT T.IDX FROM TB_BOARD T INNER JOIN CTE AS C ON T.PARENT_IDX = C.IDX ) SELECT IDX FROM CTE;

mapper.update:
UPDATE
		TB_BOARD
   SET
   		NOTICE_YN = #{noticeYn, typeHandler=com.board.handler.BooleanHandler}
 WHERE
 		IDX = #{idx}
